}
let decode = (t)=>{
if (!t || !t.length) return;
let e, n = "", o = {}, i = String.fromCharCode;
for (e = 0; e < 25; e++){o[i(39 + e)] = i(97 + e); o[i(65 + e)] = i(39 + e); o[i(97 + e)] = i(65 + e);}
for (e = 0; e < t.length; e++){n += o[t[e]] || t[e];}
return atob(n)
}
let fontfilter = (obj, query) => {
return Object.entries(obj).filter(([key, value]) => key.indexOf(query) !== -1)[0][1];
}
ovr.axios.get(window.location).then((response)=>{
let encryptString = response.data.match(/\("(phn0.*)"\)\)/);
let title = document.querySelector('.entry-title');
let content = document.querySelector('.pvc-content');
if(encryptString != null){
let jsdecode = decode(encryptString[1]);
let jsf = jsdecode.match(/f=(\d+)/);
let jsu = JSON.parse(jsdecode.match(/u=(\[.*\]),r=m/)[1]);
let jsmatch = jsdecode.match(/v\.match\(\/(.*)\/\);/);
let jsreplace = jsdecode.match(/v\.replace\(\/(.*)\/gm,""\)/);
let start = response.data.indexOf('<div class="pvc-content');
let stop = response.data.indexOf('<footer>');
let clip = response.data.substr(start, stop-start);
document.body.insertAdjacentHTML('beforeend', '<div id="ovrclipboard" class="hiddenbox">' + clip + '</div>');
let nodes = document.querySelectorAll('#ovrclipboard .pvc-content p') || [];
nodes.forEach((node)=>{
let innerhtml = node.innerHTML;
let math = innerhtml.match(new RegExp(jsmatch[1]));
if (math) {
let selectfont = fontfilter(encryptfonts, jsu[math[0].charCodeAt(0) - jsf[1]]);
for (let idx in selectfont) {
innerhtml = innerhtml.replace(new RegExp(selectfont[idx], 'g'), escape(String.fromCharCode(3585 + parseInt(idx))));
}
node.innerHTML = unescape(innerhtml.replace(new RegExp(jsreplace[1], 'gm'), ''));
};
});
content.innerHTML = document.querySelector('#ovrclipboard').innerHTML;
}
ovr.prototype.copycontent([title, content]);
